    function dateConvert(
        string|null $date,
        string $format = 'Y-m-d H:i:s',
        DateTimeZone $from = new DateTimeZone('UTC'),
        DateTimeZone $to = new DateTimeZone('UTC'),
        bool $throwException = false
    ): string {

        return Date::convert(
            date: $date,
            format: $format,
            from: $from,
            to: $to,
            throwException: $throwException
        );

        // 
    }
}

