if (
    !function_exists('resolveRequest')
) {
    function resolveRequest(
        string $request,
        array $data = [],
        User|null $user = null
    ) {

        $req = new $request();

        $req->merge($data);

        if (
            filled($user)
        ) {
            $req->setUserResolver(fn() => $user);
        }

        $container = app();
        $req
            ->setContainer($container)
            ->setRedirector($container->make('redirect'))
            ->validateResolved();

        return $req;
    }
}

if (
    !function_exists('dbTransaction')
) {

    function dbTransaction(callable $callback, bool $useLogBatch = true): mixed
    {
        try {

            DB::beginTransaction();

            if ($useLogBatch) {
                LogBatch::startBatch();
            }

            $result = $callback();

            if ($useLogBatch) {
                LogBatch::endBatch();
            }

            DB::commit();

            return $result;

            // 
        } catch (Exception $e) {

            if ($useLogBatch) {
                LogBatch::endBatch();
            }

            DB::rollBack();

            throw $e;
        }
    }
}

if (
    !function_exists('jsonAttribute')
) {
    function jsonAttribute(): Attribute
    {
        return Attribute::make(
            get: fn($value) => filled(emptyToNullOrValue($value)) ? jsonDecodeToArray($value) : [],
            set: fn($value) => filled(emptyToNullOrValue($value)) ? jsonEncode($value)        : null,
        );
    }
}

if (
    !function_exists('userInfo')
) {
    function userInfo(
        Model $model,
        string $key
    ): array {

        if (
            !$model->relationLoaded($key)
        ) {
            return [
                'id'                    => 0,
                'country_id'            => 0,
                'full_name'             => '',
                'country_code'          => '',
                'phone_number'          => '',
                'phone_number_original' => '',
                'type'                  => __(key: 'msg.unknown'),
            ];
        }

        $user = $model?->{$key};

        return [
            'id'                        => (float) ($user?->id ?? 0),

            'country_id'                => (float) ($user?->country_id ?? 0),

            'full_name'                 => (string) ($user?->full_name ?? $user?->name ?? trim(($user?->first_name ?? '') . ' ' . ($user?->last_name ?? ''))),

            'country_code'              => (string) ($user?->country_code ?? ''),

            'phone_number'              => (string) ($user?->phone_number ?? ''),

            'phone_number_original'     => (string) ($user?->getRawOriginal('phone_number', '') ?? ''),

            'type'                      => $user?->objectName()['type'] ?? __(key: 'msg.unknown'),
        ];
    }
}


if (
    !function_exists('getUserable')
) {

    function getUserable(
        string $able,
        Request|Model|null $user = null,
        bool $throwException = false,
    ): array {

        $user = is_null($user) ? request()->user() : (($user instanceof Request) ? $user->user() : $user);
        $id = $user?->id ?? null;

        if (
            $throwException &&
            (
                blank($user) ||
                blank($id)
            )
        ) {
            throw new Exception('The user is empty');
        }

        return [
            ($able . '_type') => (filled($user) && filled($id)) ? get_class($user) : null,
            ($able . '_id')   => $id,
        ];
    }
}


